function initPlaceholders(){$("input[placeholder], textarea[placeholder]").placeholder(),$("input[placeholder]:not([placeholder-overriden]), textarea[placeholder]:not([placeholder-overriden])").attr("placeholder-overriden","1").focus(function(){var t=$(this);t.attr("placeholder-value",t.attr("placeholder")).removeAttr("placeholder")}).blur(function(){var t=$(this);t.attr("placeholder",t.attr("placeholder-value"))})}$(function(){initPlaceholders()});